<script lang="js">
	/**
	 * @type {SVGElement}
	 */
	let realitySvg;
	/**
	 * @type {SVGPathElement}
	 */
	let realityPath;

	let scrollY = 0;
	let vh = 0;

	let titleTranslate = '0';

	// todo make smoother
	function handleScroll() {
		scrollY = window.scrollY;
		if (scrollY > 0.4 * vh) titleTranslate = '30';
		else titleTranslate = (smoothInOut(scrollY / (0.4 * vh)) * 30).toFixed(3);
	}

	/**
	 *
	 * @param progress {number} between 0 and 1
	 * @returns {number} between 0 and 1 interpolated with easing
	 */
	function easeInOut(progress) {
		return progress < 0.5 ? 2 * progress * progress : -1 + (4 - 2 * progress) * progress;
	}

	/**
	 *
	 * @param progress {number} between 0 and 1
	 * @returns {number} between 0 and 1 interpolated with easing
	 */
	function smoothInOut(progress) {
		return 0.5 - 0.5 * Math.cos(Math.PI * progress);
	}
</script>

